mapper.insert:
insert into user (id, username, phone, 
  passwd, createTime, info
  )
values (#{id,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, 
  #{passwd,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, #{info,jdbcType=VARCHAR}
  )

mapper.select:
select count(*) from user
WHERE
username = #{username}

select count(*) from user
WHERE
phone = #{phone}

select

from user
where username = #{0} and passwd = #{1}

mapper.update:
update user
set passwd = #{1}
where phone = #{0}
